import { useState } from "react";
import axios from "axios";

function Form({ positions }) {
  const [name, setName] = useState("");
  const [id, setId] = useState("");
  const [fightStyle, setFightStyle] = useState("");
  const [type, setType] = useState("");
  const [position, setPosition] = useState("");

  async function handleSubmit(e) {
    e.preventDefault();

    if (!name || !id || !fightStyle || !type || !position) return;

    const newMove = { name, id, fightStyle, type, position };

    try {
      const response = await axios.post(
        "http://localhost:5000/api/moves",
        newMove
      );
      console.log("Move added:", response.data);
      // Clear the form or show a success message
      setNewMove({ name: "", description: "" });
    } catch (error) {
      console.error("Error adding move:", error);
    }
  }
  return (
    <div>
      <form>
        <input
          name="move"
          value={name}
          placeholder="Enter Move Name..."
          onChange={(e) => setName(e.target.value)}
        />
        <input
          name="id"
          value={id}
          placeholder="Enter YouTube ID..."
          onChange={(e) => setId(e.target.value)}
        />
        <select
          name="fightStyle"
          value={fightStyle}
          onChange={(e) => setFightStyle(e.target.value)}
        >
          <option>Select a Fight Style...</option>
          <option value="Gi">Gi</option>
          <option value="No Gi">No Gi</option>
        </select>
        <select
          name="type"
          value={type}
          onChange={(e) => setType(e.target.value)}
        >
          <option>Select a type...</option>
          <option value="Attack">Attack</option>
          <option value="Sweep">Sweep</option>
          <option value="Escape">Escape</option>
          <option value="Transition">Transition</option>
          <option value="Takedown">Takedown</option>
          <option value="Guard Pass">Guard Pass</option>
        </select>
        <select
          name="position"
          value={position}
          onChange={(e) => setPosition(e.target.value)}
        >
          <option>Select a position...</option>
          {positions.map((position) => (
            <option value={position}>{position}</option>
          ))}
        </select>
      </form>
    </div>
  );
}

export default Form;
